import React from "react";
import FavoriteContext from "../contexts/favoritesContext";

const { useContext } = React;

const Navbar = () => {
  const { favoritePokemons } = useContext(FavoriteContext);

  let imgUrl =
    "https://raw.githubusercontent.com/PokeAPI/media/master/logo/pokeapi_256.png";

  return (
    <nav>
      <div />
      <div>
        <img src={imgUrl} alt="pokeapi-logo" className="navbar-image" />
      </div>
      <div>
      <h2>Bienvenido a la Pokedex</h2>
      <p>es una enciclopedia electrónica portátil que los entrenadores Pokémon llevan consigo para</p>
        <p>registrar automáticamente las fichas de todas las diversas especies Pokémon vistas y capturadas</p>
        <p>durante su viaje como entrenadores.</p>
      </div>
      <div>&#10084;&#65039; {favoritePokemons.length}</div>
    </nav>
  );
};

export default Navbar;
